#ifdef PXT64
#define CMPOP_RAW(op, t, f)                                                                        \
    if (bothNumbers(a, b))                                                                         \
        return numValue(a) op numValue(b) ? t : f;                                                 \
    int cmp = valCompare(a, b);                                                                    \
    return cmp != -2 && cmp op 0 ? t : f;
#else
