            case SoundExpressionEffect.Warble:
                src = setValue(src, 36, DAL.SFX_DEFAULT_WARBLE_PARAM, 4);
                src = setValue(src, 40, DAL.SFX_DEFAULT_WARBLE_STEPS, 4);
                break;
        }

        return src;
    }

