#ifndef CODAL_FLASH_H
#define CODAL_FLASH_H

#include "CodalDevice.h"

namespace codal {
class Flash {
  public:
    /**
     * Return page size in bytes at given address (doesn't have to be page-aligned).
     */
    virtual int pageSize(uintptr_t address) = 0;

    /**
     * Erase page at given page-aligned address.
     */
    virtual int erasePage(uintptr_t address) = 0;

    /**
     * Write given number of bytes within one page. Flash has to be erased first.
     */
    virtual int writeBytes(uintptr_t dst, const void *src, uint32_t len) = 0;

    /**
     * Return the total size of flash.
     */
    virtual int totalSize();
};

class ZFlash : public Flash {
  public:
    virtual int pageSize(uintptr_t address);
    virtual int totalSize();
    virtual int erasePage(uintptr_t address);
    virtual int writeBytes(uintptr_t dst, const void *src, uint32_t len);
};

#define CODAL_FLASH codal::ZFlash
#define DEVICE_FLASH_ERROR 922


} // namespace codal

#endif

settings.NRF52Flash.cpp:
#include "pxt.h"
#include "Flash.h"

//#define LOG DMESG
#define LOG NOLOG

#if defined(NRF52_SERIES)
namespace codal {

#define waitForLast() while ((NRF_NVMC->READY & NVMC_READY_READY_Msk) == 0);

static volatile bool flash_op_complete = false;

#ifdef SOFTDEVICE_PRESENT
#include "nrf_sdh_soc.h"
#include "nrf_sdm.h"

static void nvmc_event_handler(uint32_t sys_evt, void *)
{
    if (sys_evt == NRF_EVT_FLASH_OPERATION_SUCCESS)
        flash_op_complete = true;
}

NRF_SDH_SOC_OBSERVER(nrfflash_soc_observer, 0, nvmc_event_handler, NULL);

#ifndef MICROBIT_CODAL
bool ble_running()
{
    uint8_t t = 0;
    sd_softdevice_is_enabled(&t);
    return t==1;
}
#endif

#endif


static inline uint32_t NRF_PAGE_SIZE() {
#if defined(FICR_INFO_CODEPAGESIZE_CODEPAGESIZE_Msk)
    return NRF_FICR->INFO.CODEPAGESIZE;
#else
    return NRF_FICR->CODEPAGESIZE;
#endif
}

int ZFlash::pageSize(uintptr_t address) {
    (void)address;
#if defined(FICR_INFO_CODEPAGESIZE_CODEPAGESIZE_Msk)
    return NRF_FICR->INFO.CODEPAGESIZE;
#else
    return NRF_FICR->CODEPAGESIZE;
#endif
}

int ZFlash::totalSize() {
#if defined(FICR_INFO_CODEPAGESIZE_CODEPAGESIZE_Msk)
    return NRF_FICR->INFO.CODESIZE * pageSize(0);
#else
    return NRF_FICR->CODESIZE * pageSize(0);
#endif
}

int ZFlash::erasePage(uintptr_t address) {
    if (address & (pageSize(address) - 1))
        target_panic(DEVICE_FLASH_ERROR);

#ifdef SOFTDEVICE_PRESENT
    if (ble_running())
    {
        flash_op_complete = false;
        while(1)
        {
            if ( sd_flash_page_erase(((uint32_t)address)/NRF_PAGE_SIZE()) == NRF_SUCCESS)
                break;

            system_timer_wait_ms(10);
        }

        while(!flash_op_complete);
    }
    else
#endif
    {
        NRF_NVMC->CONFIG = NVMC_CONFIG_WEN_Een;
        waitForLast();
        NRF_NVMC->ERASEPAGE = address;
        waitForLast();
        NRF_NVMC->CONFIG = NVMC_CONFIG_WEN_Ren;
        waitForLast();
    }

    return 0;
}

int ZFlash::writeBytes(uintptr_t dst, const void *src, uint32_t len) {
    LOG("WR flash at %p len=%d", (void *)dst, len);

    if ((dst & 3) || ((uintptr_t)src & 3) || (len & 3))
        return -1;

    for (unsigned i = 0; i < len; ++i)
        if (((uint8_t *)dst)[i] != 0xff && ((uint8_t *)src)[i] != 0xff)
            return -3;


    volatile uint32_t *sp = (uint32_t *)src;
    volatile uint32_t *dp = (uint32_t *)dst;

    len >>= 2;

#ifdef SOFTDEVICE_PRESENT
    if (ble_running())
    {
        flash_op_complete = false;

        while(1)
        {
            if (sd_flash_write((uint32_t *)dp, (uint32_t *)sp, len) == NRF_SUCCESS)
                break;

            system_timer_wait_ms(10);
        }

        while(!flash_op_complete);
    }
    else
#endif
    {
        NRF_NVMC->CONFIG = NVMC_CONFIG_WEN_Wen;
        waitForLast();

        while (len-- > 0) {
            uint32_t v = *sp++;
            if (v != 0xffffffff) {
                *dp++ = v;
                waitForLast();
            } else {
                dp++;
            }
        }

        NRF_NVMC->CONFIG = NVMC_CONFIG_WEN_Ren;
        waitForLast();
    }

    LOG("WR flash OK");

    return 0;
}
} // namespace codal
#endif

settings.RAFFS.cpp:
#include "RAFFS.h"
#include "CodalDmesg.h"
#include "NotifyEvents.h"
#include "MessageBus.h"
#include "Timer.h"
#include "pxtbase.h"

#include <stddef.h>

#define RAFFS_MAGIC 0x7776e0da
#define M1 0xffffffffU

#define CHECK
//#undef CHECK

using namespace codal;

#define oops() target_panic(DEVICE_FLASH_ERROR)

#define OFF2(v, basePtr) (uint32_t)((uint8_t *)v - (uint8_t *)basePtr)
#define OFF(v) OFF2(v, basePtr)

#define REAL_OFF(dst) (OFF(dst) <= OFF2(dst, altBasePtr()) ? OFF(dst) : OFF2(dst, altBasePtr()))

#undef NOLOG
#define NOLOG(...) ((void)0)
#ifndef RAFFS_TEST
#define LOG DMESG
#define LOGV NOLOG
#endif

#define LOGVV NOLOG

#if 0
#undef LOGV
#define LOGV DMESG
#endif

#if 0
#undef LOGVV
#define LOGVV DMESG
#endif

using namespace pxt::raffs;

static uint16_t raffs_unlocked_event;

struct FSHeader {
    uint32_t magic;
    uint32_t bytes;
    uint32_t numgc;
    uint32_t reserved;
};

static uint16_t fnhash(const char *fn) {
    uint32_t h = hash_fnv1(fn, strlen(fn));
    return h ^ (h >> 16);
}

FS::FS(Flash &flash, uintptr_t baseAddr, uint32_t bytes)
    : flash(flash), baseAddr(baseAddr), bytes(bytes) {
    locked = false;

    basePtr = NULL;
    endPtr = NULL;
    freeDataPtr = NULL;
    metaPtr = NULL;
    readDirPtr = NULL;
    cachedMeta = NULL;
    flashBufAddr = 0;
    blocked = NULL;
    gcHorizon = -10000000;
    minGCSpacing = 0;

    if (bytes > 0x20000)
        oops();

    auto page = flash.pageSize(baseAddr);
    // baseAddr and bytes needs to page-aligned, and we need even number of pages
    auto numPages = bytes / page;
    if ((baseAddr & (page - 1)) || bytes % page || numPages < 2 || (numPages & 1))
        oops();

    if (!raffs_unlocked_event)
        raffs_unlocked_event = codal::allocateNotifyEvent();
}

void FS::erasePages(uintptr_t addr, uint32_t len) {
    auto end = addr + len;
    auto page = flash.pageSize(addr);
    if (addr & (page - 1))
        oops();
    while (addr < end) {
        if (flash.pageSize(addr) != page)
            oops();
        flash.erasePage(addr);
#ifdef CHECK
        for (int i = 0; i < page; ++i)
            if (((uint8_t *)addr)[i] != 0xff)
                oops();
#endif
        addr += page;
    }
}

void FS::oopsAndClear() {
    erasePages(baseAddr, bytes);
    oops();
}

void FS::flushFlash() {
    if (flashBufAddr) {
        int r = flash.writeBytes(flashBufAddr, flashBuf, sizeof(flashBuf));
        if (r)
            oopsAndClear();
#ifdef CHECK
        for (unsigned i = 0; i < sizeof(flashBuf); ++i)
            if (flashBuf[i] != 0xff && flashBuf[i] != ((uint8_t *)flashBufAddr)[i])
                oopsAndClear();
#endif
        flashBufAddr = 0;
    }
}

void FS::writeBytes(void *dst, const void *src, uint32_t size) {
    LOGVV("write %x%s %d %x:%x:%x:%x", REAL_OFF(dst), OFF(dst) == REAL_OFF(dst) ? "" : "*", size,
          ((const uint8_t *)src)[0], ((const uint8_t *)src)[1], ((const uint8_t *)src)[2],
          ((const uint8_t *)src)[3]);

    while (size > 0) {
        uint32_t off = (uintptr_t)dst & (sizeof(flashBuf) - 1);
        uintptr_t newaddr = (uintptr_t)dst - off;
        if (newaddr != flashBufAddr) {
            flushFlash();
            memset(flashBuf, 0xff, sizeof(flashBuf));
            flashBufAddr = newaddr;
        }

        unsigned n = sizeof(flashBuf) - off;
        if (n > size)
            n = size;
        memcpy(flashBuf + off, src, n);
        size -= n;
        src = (const uint8_t *)src + n;
        dst = (uint8_t *)dst + n;
    }
}

#define IS_VALID(hd) ((hd)->magic == RAFFS_MAGIC && (hd)->bytes == bytes)

void FS::format() {
    cachedMeta = NULL;
    readDirPtr = NULL;
    clearBlocked();

    LOG("formatting...");

    FSHeader hd;
    hd.reserved = M1;
    hd.numgc = 1;

    // in case the secondary header is valid, copy #gc
    auto hd2 = (FSHeader *)(baseAddr + bytes / 2);
    if (IS_VALID(hd2)) {
        hd.numgc = hd2->numgc + 1;
    }

    // write the primary header
    erasePages(baseAddr, bytes / 2);
    hd.magic = RAFFS_MAGIC;
    hd.bytes = bytes;
    writeBytes((void *)baseAddr, &hd, sizeof(hd));

    flushFlash();
}

#define NUMBLOCKED (int)(sizeof(blocked->fnptrs) / sizeof(uint16_t))

bool FS::checkBlocked(MetaEntry *m) {
    auto fnptr = m->fnptr;
    for (auto p = blocked; p; p = p->next) {
        for (int i = 0; i < NUMBLOCKED; ++i)
            if (p->fnptrs[i] == fnptr) {
                if (m->isFirst())
                    p->fnptrs[i] = 0;
                return true;
            }
    }
    if (!m->isFirst()) {
        for (auto p = blocked; p; p = p->next) {
            for (int i = 0; i < NUMBLOCKED; ++i)
                if (p->fnptrs[i] == 0) {
                    p->fnptrs[i] = fnptr;
                    return false;
                }
        }
        auto p = new BlockedEntries;
        memset(p, 0, sizeof(*p));
        p->next = blocked;
        blocked = p;
        p->fnptrs[0] = fnptr;
    }
    return false;
}

void FS::clearBlocked() {
    while (blocked) {
        auto p = blocked;
        blocked = p->next;
        delete p;
    }
}

bool FS::tryMount() {
    if (basePtr)
        return true;

    auto hd0 = (FSHeader *)baseAddr;
    auto hd1 = (FSHeader *)(baseAddr + bytes / 2);

    auto v0 = IS_VALID(hd0);
    auto v1 = IS_VALID(hd1);

    if (v0 && v1) {
        // we account for overflows
        // they should not occur in normal operation though
        if (hd0->numgc + 1 == hd1->numgc)
            v0 = false;
        else if (hd1->numgc + 1 == hd0->numgc || hd1->numgc < hd0->numgc)
            v1 = false;
        else
            v0 = false;
    }

    uintptr_t addr;

    if (v0)
        addr = baseAddr;
    else if (v1)
        addr = baseAddr + bytes / 2;
    else
        return false;

    basePtr = (uint8_t *)addr;
    endPtr = (MetaEntry *)(addr + bytes / 2);

    auto p = (uint32_t *)endPtr - 2;
    while (*p != M1)
        p -= 2;
    metaPtr = (MetaEntry *)(p + 2);

    p = (uint32_t *)metaPtr - 1;
    while (*p == M1)
        p--;
    freeDataPtr = (uint8_t *)RAFFS_ROUND(p + 1);

    auto fp = (uint32_t *)freeDataPtr;
    if (fp[0] != M1 || fp[1] != M1)
        oopsAndClear();

    LOG("mounted, end=%x meta=%x free=%x", OFF(endPtr), OFF(metaPtr), OFF(freeDataPtr));

    return true;
}

void FS::mount() {
    // if (basePtr) return;
    if (tryMount())
        return;
    format();
    if (!tryMount())
        oopsAndClear();
}

FS::~FS() {}

int FS::write(const char *keyName, const void *data, uint32_t bytes) {
    auto isDel = data == NULL && bytes == M1;
    if (!isDel && !data && bytes)
        oops();

    if (isDel)
        LOGV("del: %s", keyName);
    else
        LOGV("write: %s sz=%d", keyName, bytes);

    lock();
    uint32_t szneeded = bytes;
    auto existing = findMetaEntry(keyName);
    auto prevBase = basePtr;

    cachedMeta = NULL;

    if (!existing) {
        if (isDel) {
            unlock();
            return -1;
        }
        szneeded += strlen(keyName) + 1;
    }

    if (!tryGC(sizeof(MetaEntry) + RAFFS_ROUND(szneeded))) {
        unlock();
        return -1;
    }

    // if the GC happened, find the relocated meta entry
    if (prevBase != basePtr)
        existing = findMetaEntry(keyName);

    MetaEntry newMeta;
    if (existing) {
        newMeta.fnhash = existing->fnhash;
        newMeta.fnptr = existing->fnptr;
    } else {
        newMeta.fnhash = fnhash(keyName);
        newMeta.fnptr = writeData(keyName, strlen(keyName) + 1);
    }
    newMeta.dataptr = isDel ? 0 : writeData(data, bytes);
    newMeta._datasize = bytes;
    if (existing)
        newMeta._datasize |= RAFFS_FOLLOWING_MASK;
    finishWrite();

    writeBytes(--metaPtr, &newMeta, sizeof(newMeta));
    flushFlash();

    unlock();
    return 0;
}

int FS::read(const char *keyName, void *data, uint32_t bytes) {
    lock();
    int r = -1;
    MetaEntry *meta;
    if (keyName) {
        cachedMeta = meta = findMetaEntry(keyName);
    } else {
        meta = cachedMeta;
    }
    if (meta != NULL && meta->dataptr) {
        r = meta->datasize();
        if (data) {
            if (bytes > (unsigned)r)
                bytes = r;
            memcpy(data, basePtr + meta->dataptr, bytes);
        }
    }
    unlock();
    return r;
}

int FS::remove(const char *keyName) {
    return write(keyName, NULL, M1);
}

void FS::lock() {
    while (locked)
        fiber_wait_for_event(DEVICE_ID_NOTIFY, raffs_unlocked_event);
    locked = true;
    mount();
}

void FS::unlock() {
    if (!locked)
        oops();
    flushFlash();
    locked = false;
#ifndef RAFFS_TEST
    Event(DEVICE_ID_NOTIFY, raffs_unlocked_event);
#endif
}

MetaEntry *FS::findMetaEntry(const char *filename) {
    uint16_t h = fnhash(filename);
    uint16_t buflen = strlen(filename) + 1;

    for (auto p = metaPtr; p < endPtr; p++) {
        // LOGV("check at %x %x %x", OFF(p),p->fnhash,h);
        if (p->fnhash == h && memcmp(fnptr(p), filename, buflen) == 0)
            return p;
    }

    // LOGV("fail");

    return NULL;
}

void FS::forceGC(filename_filter filter) {
    lock();
    tryGC(0x7fff0000, filter);
    unlock();
}

bool FS::tryGC(int spaceNeeded, filename_filter filter) {
    int spaceLeft = (intptr_t)metaPtr - (intptr_t)freeDataPtr;

#ifdef RAFFS_TEST
    for (auto p = (uint32_t *)freeDataPtr; p < (uint32_t *)metaPtr; p++) {
        if (*p != M1) {
            LOG("value at %x = %x", OFF(p), *p);
            oopsAndClear();
        }
    }
#endif

    if (spaceLeft > spaceNeeded + 32)
        return true;

    int now = (int)system_timer_current_time();
    if (minGCSpacing) {
        gcHorizon += minGCSpacing;
        int nextGC = now - minGCSpacing * 2;
        // LOG("now=%d n=%d gch=%d", now, nextGC, gcHorizon);
        if (nextGC > gcHorizon)
            gcHorizon = nextGC;
        if (gcHorizon > now)
            target_panic(921);
    }

    LOG("running flash FS GC; needed %d, left %d", spaceNeeded, spaceLeft);

    readDirPtr = NULL;
    cachedMeta = NULL;

    auto newBase = (uintptr_t)altBasePtr();

    flushFlash();

    erasePages(newBase, bytes / 2);

    auto metaDst = (MetaEntry *)(newBase + bytes / 2);
    auto newBaseP = (uint8_t *)newBase;
    freeDataPtr = newBaseP + sizeof(FSHeader);

    for (int iter = 0; iter < 2; ++iter) {
        clearBlocked();
        auto offset = sizeof(FSHeader);
        for (auto p = metaPtr; p < endPtr; p++) {
            MetaEntry m = *p;
            const char *fn = fnptr(&m);

            if (filter && !filter(fn))
                continue;

            if (checkBlocked(&m) || m.dataptr == 0)
                continue;

            LOGV("GC %s sz=%d @%x", fn, m.datasize(), m.dataptr);
            auto fnlen = strlen(fn) + 1;
            auto sz = fnlen + m.datasize();

            if (iter == 0) {
                auto fd = freeDataPtr;
                writeData(fn, fnlen);
                writeData(basePtr + m.dataptr, m.datasize());
                if (freeDataPtr - fd != (int)sz)
                    oops();
            } else {
                m.fnptr = offset;
                m.dataptr = offset + fnlen;
                m._datasize &= ~RAFFS_FOLLOWING_MASK;
                writeBytes(--metaDst, &m, sizeof(m));
            }
            offset += sz;
        }
        if (iter == 0)
            finishWrite();
    }

    clearBlocked();
    flushFlash();

    LOG("GC done: %d free", (int)((intptr_t)metaDst - (intptr_t)freeDataPtr));

    FSHeader hd;
    hd.magic = RAFFS_MAGIC;
    hd.bytes = bytes;
    hd.numgc = ((FSHeader*)basePtr)->numgc + 1;
    hd.reserved = M1;
    writeBytes(newBaseP, &hd, sizeof(hd));
    flushFlash();

    basePtr = newBaseP;
    endPtr = (MetaEntry *)(newBase + bytes / 2);
    metaPtr = metaDst;

    if ((intptr_t)metaDst - (intptr_t)freeDataPtr <= spaceNeeded + 64) {
        if (filter != NULL && spaceNeeded != 0x7fff0000) {
            LOG("out of space! needed=%d", spaceNeeded);
#ifdef RAFFS_TEST
            oops();
#endif
        }
        return false;
    }

    return true;
}

DirEntry *FS::dirRead() {
    lock();

    if (readDirPtr == NULL) {
        readDirPtr = metaPtr;
        clearBlocked();
    }

    while (readDirPtr < endPtr) {
        auto m = *readDirPtr++;
        if (checkBlocked(&m) || m.dataptr == 0)
            continue;
        dirEnt.size = m.datasize();
        dirEnt.flags = 0;
        dirEnt.name = fnptr(&m);
        unlock();
        return &dirEnt;
    }

    readDirPtr = NULL;
    clearBlocked();
    unlock();
    return NULL;
}

uint16_t FS::writeData(const void *data, uint32_t len) {
    LOGVV("writeData: @%x %x:%x sz=%d", REAL_OFF(freeDataPtr), ((const uint8_t *)data)[0],
          ((const uint8_t *)data)[1], len);
    writeBytes(freeDataPtr, data, len);
    auto r = freeDataPtr - basePtr;
    freeDataPtr += len;
    return r;
}

void FS::finishWrite() {
    auto nfp = RAFFS_ROUND(freeDataPtr);
    int tailSz = nfp - (uintptr_t)freeDataPtr;
    uint64_t z = 0;
    if (tailSz) {
        writeData(&z, tailSz);
    } else {
        if (((uint32_t *)nfp)[-1] == M1)
            writeData(&z, 8);
    }
    flushFlash();
}

int FS::readFlashBytes(uintptr_t addr, void *buffer, uint32_t len) {
    lock();
    memcpy(buffer, (void *)addr, len);
    unlock();
    return len;
}

#ifdef RAFFS_TEST
void FS::dump() {}

void FS::debugDump() {
    // dump();
}
#endif

settings.RAFFS.h:
#ifndef CODAL_RAFFS_H
#define CODAL_RAFFS_H

#include "Flash.h"

namespace pxt {
namespace raffs {

struct DirEntry {
    uint32_t size;
    uint16_t flags;
    const char *name;
};

struct BlockedEntries {
    BlockedEntries *next;
    uint16_t fnptrs[32];
};

#define RAFFS_FOLLOWING_MASK 0x8000

#ifdef PICO_BOARD
#define RAFFS_FLASH_BUFFER_SIZE 256
#else
#define RAFFS_FLASH_BUFFER_SIZE 64
#endif

struct MetaEntry {
    uint16_t fnhash;    // hash of file name
    uint16_t fnptr;     // offset in bytes; can't be 0xffff
    uint16_t _datasize; // size in bytes; highest bit is set if this isn't first block
    uint16_t dataptr;   // offset in bytes; 0x0 - deleted

    uint16_t datasize() { return _datasize & 0x7fff; }
    bool isFirst() { return (_datasize & RAFFS_FOLLOWING_MASK) == 0; }
};

#define RAFFS_ROUND(x) ((((uintptr_t)(x) + 7) >> 3) << 3)

typedef bool (*filename_filter)(const char *);

class FS {
    codal::Flash &flash;

    uint8_t *basePtr, *freeDataPtr;
    MetaEntry *endPtr, *metaPtr, *readDirPtr, *cachedMeta;
    int32_t gcHorizon;
    DirEntry dirEnt;
    uintptr_t flashBufAddr;
    uint8_t flashBuf[RAFFS_FLASH_BUFFER_SIZE];
    BlockedEntries *blocked;
    volatile bool locked;

    void erasePages(uintptr_t addr, uint32_t len);
    void flushFlash();
    void writeBytes(void *dst, const void *src, uint32_t size);
    void mount();
    void lock();
    void unlock();
    MetaEntry *findMetaEntry(const char *filename);
    bool tryGC(int spaceNeeded, filename_filter filter = NULL);

    bool checkBlocked(MetaEntry *m);
    void clearBlocked();
    void oopsAndClear();

    uint16_t writeData(const void *data, uint32_t len);
    void finishWrite();
    const char *fnptr(MetaEntry *m) { return (const char *)(basePtr + m->fnptr); }

    uint32_t *altBasePtr() {
        if ((uintptr_t)basePtr == baseAddr)
            return (uint32_t *)(baseAddr + bytes / 2);
        else
            return (uint32_t *)baseAddr;
    }

  public:
    // Minimum time in ms that has to pass between two GCs; we shall panic 920 if GCs happen more often
    // (avareged over 3x this time).
    // This is usually set to around 10s (10000), so that if user writes a program that writes to flash in
    // a loop, it doesn't wear out flash completely.
    uint16_t minGCSpacing;
    uintptr_t baseAddr;
    uint32_t bytes;

    FS(codal::Flash &flash, uintptr_t baseAddr, uint32_t bytes);
    ~FS();

    // returns 0 for success, negative for error
    int write(const char *keyName, const void *data, uint32_t bytes);
    // returns total number of bytes in key's value or -1 when file doesn't exists
    // if keyName==NULL it will re-use last keyName
    int read(const char *keyName, void *data, uint32_t bytes);
    // deletes given key if it exists
    int remove(const char *keyName);

    void format();
    bool exists(const char *keyName) { return read(keyName, NULL, 0) >= 0; }
    uint32_t totalSize() { return bytes / 2; }
    uint32_t freeSize() { return (uintptr_t)endPtr - (uintptr_t)freeDataPtr; }
    void forceGC(filename_filter filter = NULL);
    // this allow raw r/o access; will lock the instance as needed
    int readFlashBytes(uintptr_t addr, void *buffer, uint32_t len);
    bool tryMount();

    void dirRewind() { readDirPtr = NULL; }
    DirEntry *dirRead(); // data is only valid until next call to to any of File or FS function

#ifdef RAFFS_TEST
    void debugDump();
    void dump();
#else
    void debugDump() {}
#endif
};

} // namespace raffs
} // namespace pxt

#endif

settings.RP2040Flash.cpp:
#include "pxt.h"
#include "Flash.h"

//#define LOG DMESG
#define LOG NOLOG

#ifdef PICO_BOARD
#include "hardware/flash.h"

#define XIP_BIAS 0x10000000

namespace codal {

int ZFlash::pageSize(uintptr_t address) {
  return FLASH_SECTOR_SIZE;
}

int ZFlash::totalSize() {
#ifndef PICO_FLASH_SIZE_BYTES
  return 2*1024*1024;
#else
  return PICO_FLASH_SIZE_BYTES;
#endif
}

int ZFlash::erasePage(uintptr_t address) {
  // address should be aligned to 4096
  if (address % 4096 == 0){
    target_disable_irq();
    flash_range_erase(address - XIP_BIAS, FLASH_SECTOR_SIZE);  
    target_enable_irq();
  }
  return 0;
}

int ZFlash::writeBytes(uintptr_t dst, const void *src, uint32_t len) {
  if (len != FLASH_PAGE_SIZE || (dst & (FLASH_PAGE_SIZE - 1))) return -1;
  // should be aligned to 256
  target_disable_irq();
  flash_range_program(dst - XIP_BIAS, (const uint8_t*)src, FLASH_PAGE_SIZE);
  target_enable_irq();

  return 0;
}




}

#endif

settings.SAMDFlash.cpp:
#include "pxt.h"
#include "Flash.h"

//#define LOG DMESG
#define LOG NOLOG

#if defined(SAMD51) || defined(SAMD21)
namespace codal {

#ifdef SAMD51
#define waitForLast()                                                                              \
    while (NVMCTRL->STATUS.bit.READY == 0)                                                         \
        ;
#else
#define waitForLast()                                                                              \
    while (NVMCTRL->INTFLAG.bit.READY == 0)                                                        \
        ;
#endif

static void unlock() {
#ifdef SAMD51
    // see errata 2.14.1
    NVMCTRL->CTRLA.bit.CACHEDIS0 = true;
    NVMCTRL->CTRLA.bit.CACHEDIS1 = true;

    CMCC->CTRL.bit.CEN = 0;
    while (CMCC->SR.bit.CSTS) {
    }
    CMCC->MAINT0.bit.INVALL = 1;
#endif
}

static void lock() {
#ifdef SAMD51
    // re-enable cache
    NVMCTRL->CTRLA.bit.CACHEDIS0 = false;
    NVMCTRL->CTRLA.bit.CACHEDIS1 = false;

    // re-enable cortex-m cache - it's a separate one
    CMCC->CTRL.bit.CEN = 0;
    while (CMCC->SR.bit.CSTS) {
    }
    CMCC->MAINT0.bit.INVALL = 1;
    CMCC->CTRL.bit.CEN = 1;
#endif
}

int ZFlash::totalSize() {
    return (8 << NVMCTRL->PARAM.bit.PSZ) * NVMCTRL->PARAM.bit.NVMP;
}

// this returns the size of "page" that can be erased ("row" in datasheet)
int ZFlash::pageSize(uintptr_t address) {
#ifdef SAMD51
    if (address < (uintptr_t)totalSize())
        return NVMCTRL_BLOCK_SIZE; // 8k
#else
    if (address < (uintptr_t)totalSize())
        return 256;
#endif
    target_panic(DEVICE_FLASH_ERROR);
    return 0;
}

#ifdef SAMD51
#define CMD(D21, D51) NVMCTRL->CTRLB.reg = NVMCTRL_CTRLB_CMDEX_KEY | D51
#else
#define CMD(D21, D51) NVMCTRL->CTRLA.reg = NVMCTRL_CTRLA_CMDEX_KEY | D21
#endif

int ZFlash::erasePage(uintptr_t address) {
    LOG("Erase %x", address);
#ifdef SAMD51
    NVMCTRL->CTRLA.bit.WMODE = NVMCTRL_CTRLA_WMODE_MAN_Val;
#else
    NVMCTRL->CTRLB.bit.MANW = 1;
#endif
    waitForLast();
    unlock();
#ifdef SAMD51
    NVMCTRL->ADDR.reg = address;
#else
    // yeah... /2
    NVMCTRL->ADDR.reg = address / 2;
#endif
    CMD(NVMCTRL_CTRLA_CMD_ER, NVMCTRL_CTRLB_CMD_EB);
    waitForLast();
    lock();
    return 0;
}

#if 0
#define CHECK_ECC()                                                                                \
    if (NVMCTRL->INTFLAG.bit.ECCSE || NVMCTRL->INTFLAG.bit.ECCDE)                                  \
    return -10
#else
#define CHECK_ECC() ((void)0)
#endif

int ZFlash::writeBytes(uintptr_t dst, const void *src, uint32_t len) {
#ifdef SAMD51
    CHECK_ECC();

    // only allow writing double word at a time
    if (len & 7)
        return -1;
    if (dst & 7)
        return -2;

    // every double-word can only be written once, otherwise we get ECC errors
    // and no, ECC cannot be disabled
    for (unsigned i = 0; i < (len >> 3); ++i)
        if (((uint64_t *)dst)[i] != 0xffffffffffffffff &&
            ((uint64_t *)src)[i] != 0xffffffffffffffff)
            return -3;
#define WRITE_SIZE 16
#else
    if ((dst & 3) || (len & 3))
        return -1;

    for (unsigned i = 0; i < len; ++i)
        if (((uint8_t *)dst)[i] != 0xff && ((uint8_t *)src)[i] != 0xff)
            return -3;
#define WRITE_SIZE 64
#endif

    uint32_t writeBuf[WRITE_SIZE >> 2];
    uint32_t idx = 0;

    waitForLast();
    unlock();
    __DMB();

    while (idx < len) {
        uint32_t off = dst & (WRITE_SIZE - 1);
        uint32_t n = WRITE_SIZE - off;
        if (n > len - idx)
            n = len - idx;
        uint32_t *sp;
        volatile uint32_t *dp;
        if (n != WRITE_SIZE) {
            memset(writeBuf, 0xff, WRITE_SIZE);
            memcpy((uint8_t *)writeBuf + off, src, n);
            sp = writeBuf;
            dp = (uint32_t *)(dst - off);
        } else {
            sp = (uint32_t *)src;
            dp = (uint32_t *)dst;
        }

        bool need = false;
        for (unsigned i = 0; i < (WRITE_SIZE >> 2); ++i)
            if (sp[i] != 0xffffffff) {
                need = true;
                break;
            }

        if (need) {
            CMD(NVMCTRL_CTRLA_CMD_PBC, NVMCTRL_CTRLB_CMD_PBC);
            waitForLast();

            uint32_t q = WRITE_SIZE >> 2;

            target_disable_irq();
            while (q--) {
                auto v = *sp++;
                *dp = v;
                dp++;
            }

            CMD(NVMCTRL_CTRLA_CMD_WP, NVMCTRL_CTRLB_CMD_WQW);
            target_enable_irq();
            waitForLast();
        }

        src = (uint8_t *)src + n;
        dst += n;
        idx += n;
    }

    CHECK_ECC();

    lock();

    return 0;
}
} // namespace codal
#endif

settings.STM32Flash.cpp:
#include "pxt.h"
#include "Flash.h"

//#define LOG DMESG
#define LOG NOLOG

#ifdef STM32F4
namespace codal {
static void waitForLast() {
    while ((FLASH->SR & FLASH_SR_BSY) == FLASH_SR_BSY)
        ;
}

static void unlock() {
    FLASH->CR |= FLASH_CR_LOCK;
    FLASH->KEYR = FLASH_KEY1;
    FLASH->KEYR = FLASH_KEY2;
}

static void lock() {
    FLASH->CR |= FLASH_CR_LOCK;
}

int ZFlash::pageSize(uintptr_t address) {
    address |= 0x08000000;
    if (address < 0x08010000)
        return 16 * 1024;
    if (address < 0x08020000)
        return 64 * 1024;
    if (address < 0x08100000)
        return 128 * 1024;
    target_panic(DEVICE_FLASH_ERROR);
    return 0;
}

int ZFlash::totalSize() {
    return *((uint16_t *)0x1FFF7A22) * 1024;
}

int ZFlash::erasePage(uintptr_t address) {
    waitForLast();
    unlock();

    address |= 0x08000000;
    uintptr_t ptr = 0x08000000;
    int sectNum = 0;
    while (1) {
        ptr += pageSize(ptr);
        if (ptr > address)
            break;
        sectNum++;
    }

    FLASH->CR = FLASH_CR_PSIZE_1 | (sectNum << FLASH_CR_SNB_Pos) | FLASH_CR_SER;
    FLASH->CR |= FLASH_CR_STRT;

    waitForLast();

    FLASH->CR = FLASH_CR_PSIZE_1;
    lock();

    // cache flushing only required after erase, not programming (3.5.4)
    __HAL_FLASH_DATA_CACHE_DISABLE();
    __HAL_FLASH_DATA_CACHE_RESET();
    __HAL_FLASH_DATA_CACHE_ENABLE();

    // we skip instruction cache, as we're not expecting to erase that

    return 0;
}

int ZFlash::writeBytes(uintptr_t dst, const void *src, uint32_t len) {
    LOG("WR flash at %p len=%d", (void *)dst, len);

    if ((dst & 3) || ((uintptr_t)src & 3) || (len & 3))
        return -1;

    for (unsigned i = 0; i < len; ++i)
        if (((uint8_t *)dst)[i] != 0xff && ((uint8_t *)src)[i] != 0xff)
            return -3;

    waitForLast();
    unlock();

    dst |= 0x08000000;

    FLASH->CR = FLASH_CR_PSIZE_1 | FLASH_CR_PG;

    volatile uint32_t *sp = (uint32_t *)src;
    volatile uint32_t *dp = (uint32_t *)dst;
    len >>= 2;

    while (len-- > 0) {
        uint32_t v = *sp++;
        if (v != 0xffffffff) {
            *dp++ = v;
            waitForLast();
        } else {
            dp++;
        }
    }

    FLASH->CR = FLASH_CR_PSIZE_1;
    lock();

    LOG("WR flash OK");

    return 0;
}
} // namespace codal
#endif

